    public Player(MainActivity myActivity) {
        resetSpace();
        AudioManager am = (AudioManager) myActivity.getSystemService(Context.AUDIO_SERVICE);
        am.setStreamVolume(am.STREAM_MUSIC, am.getStreamMaxVolume(am.STREAM_MUSIC), 0);
        this.myActivity = myActivity;
    }

    public void changeSound (short [] newSound) {
        sound = newSound;
    }

    public void playAligner () {
        //scheduleAligner = true;
    }



    public void resetSpace () {
        spaceCounter = SPACE + tweakBy;
        tweakBy = 0;
    }

    public void setSpace (int new_space) { SPACE = new_space; }
    public int getSpace () { return SPACE; }

    public void turnOnSound () {
        scheduleTurnOn = true;
    }

    public void turnOffSound () {
        silenceOverride = true;
    }
    public boolean isSoundOn () { return !silenceOverride; }


    public void tweak (int amount) {
        //pauseSound = true;
        this.tweakBy = amount;
    }

    public void setSoftwareVolume (double softwareVolume) {
        //this.softwareVolume = 0;
        //this.softwareVolume = softwareVolume;
        AudioManager am = (AudioManager) myActivity.getSystemService(Context.AUDIO_SERVICE);
        int maxVol = am.getStreamMaxVolume(am.STREAM_MUSIC);
        int setVolume = (int) (((float)maxVol) * softwareVolume);
        Log.v(TAG, "Setting volume to " + setVolume + " out of " + maxVol);
        am.setStreamVolume(am.STREAM_MUSIC, setVolume, 0);
    }

    public double getSoftwareVolume () { return softwareVolume; }

    public void startPlaying () {
        // Set up audiotrack, start playing on loop until time to stop

        buffsize = AudioTrack.getMinBufferSize(44100, AudioFormat.CHANNEL_OUT_MONO, AudioFormat.ENCODING_PCM_16BIT);
        //buffsize = 22304;
        //buffsize + 8000;
        audioTrack = new AudioTrack(AudioManager.STREAM_MUSIC, 44100, AudioFormat.CHANNEL_OUT_MONO, AudioFormat.ENCODING_PCM_16BIT, buffsize, AudioTrack.MODE_STREAM);
        audioTrack.play();
        isRunning = true;

        new Thread(new Runnable() {
            @Override
            public void run() {
                android.os.Process.setThreadPriority(android.os.Process.THREAD_PRIORITY_URGENT_AUDIO);
                playLoop();
            }
        }).start();
    }

    public void stopPlaying() {
        isRunning = false;
        if (audioTrack != null) {
            audioTrack.stop();
            audioTrack.release();
        }
    }

    public void playLoop() {
        short samples [] = new short[buffsize];
        int chirpCount = 0;
        chirpPlayCount = 0;
        boolean inChirp = true;

        boolean inAligner = false;
        int alignerCount = 0;

        while (isRunning) {
            for (int i = 0; i < buffsize; i++) {
                if (inChirp) {
                    // MAKE SOUND
                    if (silenceOverride) samples[i] = 0;
                    else if (inAligner) {
                        samples[i] = ALIGNCHIRP[alignerCount];
                        alignerCount++;
                        if (alignerCount >= ALIGNCHIRP.length) {
                            alignerCount = 0;
                            inAligner = false;
                        }
                    } else samples[i] = sound[chirpCount];


                    // ADD COUNTER
                    chirpCount++;
                    if (chirpCount >= sound.length) inChirp = false;

                    // POSSIBLE CHANGE MODE
                    if (chirpCount >= sound.length) { // The last one, next would overflow
                        if (silenceOverride == false) {
                            chirpPlayCount ++;
                            Log.v(TAG, "Chirp count: " + chirpPlayCount);
                        }
                        chirpCount = 0;
                    }
                } else {
                    // MAKE SOUND
                    if (inAligner) {
                        samples[i] = ALIGNCHIRP[alignerCount];
                        alignerCount++;
                        if (alignerCount >= ALIGNCHIRP.length) {
                            alignerCount = 0;
                            inAligner = false;
                        }
                    } else samples[i] = 0;

                    // ADD COUNTER
                    spaceCounter--;
                    if (spaceCounter <= 0) inChirp = true;

                    // POSSIBLE CHANGE MODE
                    if (spaceCounter <= 0) {
                        if (scheduleTurnOn) {
                            silenceOverride = false;
                            scheduleTurnOn = false;
                            Log.v(TAG, "Turning on. Current count: " + chirpPlayCount);
                        }

                        if (scheduleAligner) {
                            inAligner = true;
                            scheduleAligner = false;
                        }


                        if (countMode && chirpPlayCount >= TOTALCHIRP) {
                            //Log.v(TAG, "Done chirping!");
                            silenceOverride = true;
                        }
                        resetSpace();
                    }
                }
            }


            audioTrack.write(samples, 0, buffsize);
        }
    }
